import flaskimport flask_wtfimport wtformsfrom wtforms import validators as validclass UserCvForm(flask_wtf.FlaskForm):    username   = wtforms.StringField("Username", validators=[valid.DataRequired(message="Username can't be empty")])    fathername = wtforms.StringField("Father", validators=[valid.DataRequired(message="father can't be empty")])    email      = wtforms.StringField("Email", validators=[valid.DataRequired(), valid.Email()])    age        = wtforms.IntegerField("Age", validators=[valid.DataRequired(message="Age can't be empty")])    city       = wtforms.StringField("City", validators=[valid.DataRequired(message='city can\'t be empty')])    description=wtforms.StringField("Description", validators=[valid.DataRequired(message='city can\'t be empty')])    submit = wtforms.SubmitField("Add Address")class WorkExperience(flask_wtf.FlaskForm):    title                  = wtforms.StringField("Work Title", validators=[valid.DataRequired(message="This can't be empty")])    work                   = wtforms.StringField("Work explanation", validators=[valid.DataRequired(message="This can't be empty")])    entery_date            = wtforms.DateTimeField('Start Date', format='%Y-%m-%d')    exit_date              = wtforms.DateTimeField('Start Date', format='%Y-%m-%d')    experience_description = wtforms.TextAreaField('Description')    submit = wtforms.SubmitField("Add Experience")class Project(flask_wtf.FlaskForm):    project             = wtforms.StringField("project", validators=[valid.DataRequired(message="This can't be empty")])    project_description = wtforms.TextAreaField('Project Description')    submit = wtforms.SubmitField("Add Project")class Skill(flask_wtf.FlaskForm):    skill              = wtforms.StringField("Skill", validators=[valid.DataRequired(message="This can't be empty")])    skill_description  = wtforms.TextAreaField('Skill Description')    submit = wtforms.SubmitField("Add Skill")class Education(flask_wtf.FlaskForm):    education             = wtforms.StringField("Education", validators=[valid.DataRequired(message="This can't be empty")])    university            = wtforms.StringField("University",validators=[valid.DataRequired(message="This can't be empty")])    graduate_date         = wtforms.DateTimeField('Graduate Date', format='%Y-%m-%d')    education_description = wtforms.TextAreaField('Education Description')    submit = wtforms.SubmitField("Add Education")class Language(flask_wtf.FlaskForm):    language = wtforms.StringField("Language", validators=[valid.DataRequired(message="This can't be empty")])    submit     = wtforms.SubmitField("Add Language")# class GiantForm(flask_wtf.FlaskForm):#     userCvForm      = wtforms.FormField(UserCvForm)#     workExperience  = wtforms.FormField(WorkExperience)#     project         = wtforms.FormField(Project)#     skill           = wtforms.FormField(Skill)#     education       = wtforms.FormField(Education)#     language        = wtforms.FormField(Language)#     submit          = wtforms.SubmitField('Submit All')